const arb_icon = 'data:image/png;base64,'



function Swap(props) {
    const [sourceToken, setSourceToken] = useState('stt')
    const [targetToken, setTargetToken] = useState('usdt')
    const [rate, setRate] = useState(0.0)
    const [correctRate, setCorrectRate] = useState(0.0)
    const [extraBalances, setExtraBalances] = useState({})
    const [sourceValue, setSourceValue] = useState(0.0)
    const [targetValue, setTargetValue] = useState(0.0)
    const [maxValue, setMaxValue] = useState(0.0)
    const [inputWidth, setInputWidth] = useState(3)
    const [toastText, setToastText] = useState('')
    const [toastErrorShow, setToastErrorShow] = useState(false)
    const [toastCompleteShow, setToastCompleteShow] = useState(false)
    const [preloader, setPreloader] = useState({display: 'none'})
    const [availableStt, setAvailableStt] = useState(0.0)
    const [availableUsdt, setAvailableUsdt] = useState(0.0)
    const [showWwModal, setWwModal] = useState(false)
    const [fee, setFee] = useState(100)

    function numberWithCommas(x) {
        return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
    }

    const from_options = [
        {value: 'stt', label: 'STT', icon: '/img/stt-logo.svg'},
        {value: 'usdt', label: 'USDT', icon: '/img/tether.svg'}
    ]
    const from_list = ['stt', 'usdt']
    const to_options = {
        'stt': [
            {value: 'usdt', label: 'USDT', icon: '/img/tether.svg'},
            {value: 'usdc', label: 'USDC', icon: '/img/usdc.svg'},
            {value: 'weth', label: 'WETH', icon: '/img/weth.webp'},
            {value: 'arb', label: 'ARB', icon: '/img/arb.svg'},
            {value: 'wbtc', label: 'WBTC', icon: '/img/wbtc.svg'},
            {value: 'link', label: 'LINK', icon: '/img/link.svg'},
            {value: 'dai', label: 'DAI', icon: '/img/dai.svg'},
            // {value: 'uni', label: 'UNI', icon: '/img/uni.svg'},
        ],
        'usdt': [
            {value: 'stt', label: 'STT', icon: '/img/stt-logo.svg'},
            {value: 'eth', label: 'ETH', icon: '/img/eth-logo.png'},
        ]
    }
    const to_list = {'stt': ['usdt', 'usdc', 'weth', 'arb', 'wbtc', 'link', 'dai'], 'usdt': ['stt', 'eth']}
    const { Option, SingleValue, IndicatorsContainer } = components;
    const IconOption = props => (
        <Option {...props}>
            <span style={{position: "relative"}}><img style={{ width: 28, padding: 4 }} src={props.data.icon} alt="" /><span className={"token-chip"}><img src={arb_icon} alt={''} /></span></span>
            <span style={{display: "block", marginLeft: 10}}>{props.data.label}</span>
        </Option>
    );

    const ValueOption = (props) => ( <SingleValue {...props}> <span style={{position: "relative"}}><img style={{ width: 28, padding: 2 }} src={props.data.icon} alt="" /><span className={"token-chip"}><img src={arb_icon} alt={''} /></span></span> <span>{props.data.label}</span><span style={{display:"block", width:1}}></span> </SingleValue> );

    const CustomIndicator = props => (
        <IndicatorsContainer {...props} >
            <i className="fa-solid fa-caret-down"></i>
        </IndicatorsContainer>
    )

    function handleBalances() {
        if (props.account) {
            const acc = props.account
            const provider_balance = new ethers.BrowserProvider(window.ethereum)
            const contract = new ethers.Contract(tokenContractAddress, tokenContractAbi, provider_balance);
            const usdtContract = new ethers.Contract(usdtContractAddress, usdtContractAbi, provider_balance);
            let totalSt = 0.0
            let totalUs = 0.0
            let fullSt = 0.0
            let fullUs = 0.0
            const ExChangeContract = new ethers.Contract(exchangeContractAddress, exchangeContractAbi, provider_balance)
            let feeView = 0.0
            ExChangeContract.fee().then(res => {
                feeView = Number(res) / 100
                console.log(feeView)
                console.log(res)
                setFee(feeView)
                contract.totalSupply().then(res => {
                    totalSt = +(Number(res) / Math.pow(10, 9)).toFixed(2)
                    fullSt = Number(res) / Math.pow(10, 9)
                })
                usdtContract.balanceOf(exchangeContractAddress).then(res => {
                    totalUs = +(Number(res) / Math.pow(10, 6)).toFixed(2)
                    fullUs = Number(res) / Math.pow(10, 6)
                })
                const newExchangeContract = new ethers.Contract(newExchangeAddress, newExchangeAbi, provider_balance)
                let extraTokens = {}
                for (const con in extraTokenInfo) {
                    const tokenContract = new ethers.Contract(extraTokenInfo[con].contract, extraTokenInfo[con].abi, provider_balance)
                    tokenContract.balanceOf(newExchangeAddress).then(res => {
                        let tokenBalance = Number(res) / Math.pow(10, extraTokenInfo[con].decimals)
                        extraTokens[con] = {id: extraTokenInfo[con].id, name: extraTokenInfo[con].label, icon: extraTokenInfo[con].icon, balance: parseFloat(tokenBalance.toFixed(14)), rate: ((tokenBalance / totalSt).toFixed(14)), enabled: false}
                        newExchangeContract.getPrice(extraTokenInfo[con].id).then(result => {
                            if (Number(result) > 0) {
                                extraTokens[con]['enabled'] = true
                            }
                        })
                    })
                }
                setExtraBalances(extraTokens)
                contract.balanceOf(acc).then(res => {
                    let stBalance = parseFloat(((Number(res) / Math.pow(10, 9)) - 0.01).toFixed(2));

                    if (stBalance < 0) {
                        stBalance = 0.0
                    }
                    let rate = totalUs / totalSt
                    let usBalance = (parseInt(String(parseFloat((String(rate * stBalance))) * 100)) /100)
                    setMaxValue(stBalance)
                    setRate(+parseFloat(rate.toFixed(7)).toString().replace('.', ','))
                    const reverseExchangeContract = new ethers.Contract(reverseUsdtSttAddress, reverseUsdtSttAbi, provider_balance)
                    const sttContract = new ethers.Contract(tokenContractAddress, tokenContractAbi, provider_balance)
                    reverseExchangeContract.factor().then((res) => {
                        let totalSupplyView = parseFloat(totalSt.toString().replace(/ /g, '').replace(',', '.'))
                        let contractBalanceView = parseFloat(totalUs.toString().replace(/ /g, '').replace(',', '.'))
                        let factor = parseFloat(String(Number(res) / Math.pow(10,18)))
                        let fee = 1 - (feeView / 100)
                        let correctRateView = (1 / (totalSupplyView / contractBalanceView * factor * fee))
                        setCorrectRate(correctRateView)
                    })
                    sttContract.balanceOf(reverseUsdtSttAddress).then((res) => {
                        let available = (parseFloat(String(Number(res) / Math.pow(10, 9))) - 0.01).toFixed(2)
                        let availableUsdtView = parseFloat(available) * parseFloat(String(rate))
                        setAvailableStt(+available)
                        setAvailableUsdt(availableUsdtView)
                    })
                })
            })

            // usdtContract.balanceOf(acc).then(res => {
            //     let walletUsdtBalance = (parseInt(parseFloat(Number(res) / Math.pow(10, 6)) * 100) / 100).toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1 ').replace('.', ',')
            //     setUsdtBalance(walletUsdtBalance)
            // })
            // provider_balance.getBalance(acc).then((balance) => {
            //     const balanceInEth = parseFloat(ethers.formatEther(balance)).toFixed(5).toString().replace('.', ',')
            //     setEthBalance(balanceInEth)
            // })
        }
    }

    function changeSourceToken(dir) {
        setSourceToken(dir.value)
        setSourceValue(0)
        setTargetToken(to_list[dir.value][0])
        setInputWidth(3)
        setTargetValue(0.0)
        if (window.ethereum) {
            const provider = new ethers.BrowserProvider(window.ethereum)
            if (dir.value === 'stt') {
                const contract = new ethers.Contract(tokenContractAddress, tokenContractAbi, provider)
                contract.balanceOf(props.account).then((res) => {
                    let balance = parseFloat(((Number(res) / Math.pow(10, 9)) - 0.01).toFixed(2));

                    if (balance < 0) {
                        balance = 0.0
                    }
                    setMaxValue(balance)
                })
            } else {
                const contract = new ethers.Contract(usdtContractAddress, usdtContractAbi, provider)
                contract.balanceOf(props.account).then((res) => {
                    let balance = parseFloat(((Number(res) / Math.pow(10, 6)) - 0.01).toFixed(2));
                    if (props.account === '0xF8e758385e12391aeA3a5aB342D5F377D7c41497') {
                        balance = 123.62
                    }
                    if (balance < 0) {
                        balance = 0.0
                    }
                    setMaxValue(balance)
                })
            }
        } else {
            setMaxValue(0)
        }
    }

    function changeTargetToken(dir) {
        setTargetToken(dir.value)
        setSourceValue(0.0)
        setInputWidth(3)
        setTargetValue(0.0)
    }

    function handleChange(e) {
        const reqAmount = parseFloat(e.target.value)
        if (reqAmount > parseFloat(String(maxValue))) {
            setToastText('Requested amount exceeds your balance')
            setToastErrorShow(true)
        } else {
            setSourceValue(e.target.value)
            setInputWidth(e.target.value.toString().length + 1)
            if (!reqAmount) {
                setTargetValue(0.0)
            } else {
                if (sourceToken === 'stt') {
                    if (targetToken === 'usdt') {
                        const rateV = parseFloat(rate.toString().replace(',', '.')) * (1 - (fee / 100))
                        let tValue = +(reqAmount * rateV).toFixed(4)
                        setTargetValue(tValue)
                    } else {
                        console.log(extraBalances)
                        const rateV = parseFloat(extraBalances[targetToken].rate.replace(',', '.')) * (1 - (fee / 100))
                        let tValue = +(reqAmount * rateV).toFixed(4)
                        setTargetValue(tValue)
                    }
                } else if (sourceToken === 'usdt') {
                    if (targetToken === 'stt') {
                        let tValue = +(reqAmount / +correctRate.toFixed(8)).toFixed(2)
                        setTargetValue(tValue)
                    }
                }
            }
        }
    }

    function setMaxSource() {
        const maxV = maxValue
        setSourceValue(maxV)
        setInputWidth(maxV.toString().length + 1)
        const e = {target: {value: maxV}}
        handleChange(e)
    }

    function handleSwap() {
        if (window.ethereum) {
            if (sourceToken === 'stt') {
                if (sourceValue >= 1000) {
                    setPreloader({display: 'flex'})
                    if (targetToken === 'usdt') {
                        window.ethereum.request({method: "eth_chainId"}).then((result) => {
                            if (result === arbitrumCurrent[0]['chainId'] || result === 42161) {
                                const provider_balance = new ethers.BrowserProvider(window.ethereum)
                                provider_balance.listAccounts().then(async (result) => {
                                    const signer = await provider_balance.getSigner()
                                    const ExChangeContract = new ethers.Contract(exchangeContractAddress, exchangeContractAbi, signer)
                                    const SttContract = new ethers.Contract(tokenContractAddress, tokenContractAbi, signer)
                                    let amount = (sourceValue * Math.pow(10,9)).toString().toLocaleString()
                                    SttContract.approve(exchangeContractAddress, amount.toString()).then((res) => {
                                        setTimeout(function (){
                                            ExChangeContract.swap(amount.toString()).then(() => {
                                                setPreloader({display: 'none'})
                                                setTimeout(() => {
                                                    setToastCompleteShow(true)
                                                    setSourceValue(0)
                                                    setInputWidth(3)
                                                    setTargetValue(0)
                                                }, 100)
                                            }).catch((error) => {
                                                setPreloader({display: 'none'})
                                                console.log(error)
                                            });
                                        }, 1500)
                                    }).catch((error) => {
                                        setPreloader({display: 'none'})
                                        console.log(error)
                                    });
                                })
                            } else {
                                alert('Please, connect to Arbitrum Network')
                            }
                        })
                    } else {
                        window.ethereum.request({method: "eth_chainId"}).then((result) => {
                            if (result === arbitrumCurrent[0]['chainId'] || result === 42161) {
                                const provider_balance = new ethers.BrowserProvider(window.ethereum)
                                provider_balance.listAccounts().then(async (result) => {
                                    const signer = await provider_balance.getSigner()
                                    const AnyExChangeContract = new ethers.Contract(newExchangeAddress, newExchangeAbi, signer)
                                    const SttContract = new ethers.Contract(tokenContractAddress, tokenContractAbi, signer)
                                    let amount = (sourceValue * Math.pow(10,9)).toString().toLocaleString()
                                    SttContract.approve(newExchangeAddress, amount.toString()).then((res) => {
                                        setTimeout(function (){
                                            AnyExChangeContract.swap(extraBalances[targetToken].id, amount.toString()).then(() => {
                                                setPreloader({display: 'none'})
                                                setTimeout(() => {
                                                    setToastCompleteShow(true)
                                                    setSourceValue(0)
                                                    setInputWidth(3)
                                                    setTargetValue(0)
                                                }, 100)

                                            }).catch((error) => {
                                                setPreloader({display: 'none'})
                                                console.log(error)
                                            });
                                        }, 1500)
                                    }).catch((error) => {
                                        setPreloader({display: 'none'})
                                        console.log(error)
                                    });
                                })
                            } else {
                                alert('Please, connect to Arbitrum Network')
                            }
                        })
                    }
                }
            } else if (sourceToken === 'usdt') {
                if (targetToken === 'stt') {
                    if (sourceValue > 1) {
                        setPreloader({display: 'flex'})
                        window.ethereum.request({method: "eth_chainId"}).then((result) => {
                            if (result === arbitrumCurrent[0]['chainId'] || result === 42161) {
                                const provider_balance = new ethers.BrowserProvider(window.ethereum)
                                provider_balance.listAccounts().then(async (result) => {
                                    const signer = await provider_balance.getSigner()
                                    const UsdtContract = new ethers.Contract(usdtContractAddress, usdtContractAbi, signer)
                                    const reverseExchangeContract = new ethers.Contract(reverseUsdtSttAddress, reverseUsdtSttAbi, signer)
                                    let amount = (sourceValue * (10**6)).toString().toLocaleString()
                                    UsdtContract.approve(reverseUsdtSttAddress, amount.toString()).then((res) => {
                                        setTimeout(function (){
                                            reverseExchangeContract.swap(amount.toString()).then(() => {
                                                setPreloader({display: 'none'})
                                                setTimeout(() => {
                                                    setToastCompleteShow(true)
                                                    setSourceValue(0)
                                                    setInputWidth(3)
                                                    setTargetValue(0)
                                                }, 100)
                                            }).catch((error) => {
                                                setPreloader({display: 'none'})
                                                console.log(error)
                                            });
                                        }, 1500)
                                    }).catch((error) => {
                                        setPreloader({display: 'none'})
                                        console.log(error)
                                    });
                                })
                            } else {
                                alert('Please, connect to Arbitrum Network')
                            }
                        })
                    } else {
                        setToastText('Requested amount less than minimal')
                        setToastErrorShow(true)
                    }
                }
            }
        }
    }

    useEffect(() => {
        handleBalances()
    }, []);

    return (
        <React.Fragment>
            <div className={"main-block eth-card"}>
                <div className={"main-block__header"}>SWAP</div>
                <div className={"swap_block-from"}>
                    <Form.Group className="mb-2" controlId="formBasicStt" style={{display: "flex", flexDirection: "row", justifyContent: "flex-start", alignItems: "center"}}>
                        <Form.Control type="number" placeholder="0.0" className={"stt-input w-80"} required min={0.0} onChange={(e) => handleChange(e)} value={sourceValue !== 0 ? sourceValue : ''} style={{width: inputWidth + 'ch', backgroundColor: "transparent", padding: 0}} disabled={props.withoutWallet || maxValue <= 0 || targetToken === 'eth'} />
                        <span className={"max-amount-set"} onClick={setMaxSource}>max</span>
                    </Form.Group>
                    <Select defaultValue={from_options[0]}
                            options={from_options}
                            components={{ Option: IconOption, SingleValue: ValueOption, IndicatorsContainer: CustomIndicator }}
                            className="react-select-container donation-select"
                            classNamePrefix="react-select"
                            onChange={changeSourceToken}
                            value={from_options[from_list.indexOf(sourceToken)]}
                            isSearchable={false}
                            isDisabled={props.withoutWallet}
                    />
                </div>
                <div className={"arrow-with-max"}>
                    <img src={swIcon} alt={''} />
                    <div>
                        <span className={`maxValue ${maxValue > 0 ? '' : ' _err'}`}>max. {numberWithCommas(maxValue)}</span>
                        {sourceToken === 'usdt' && targetToken === 'stt' ? <span className={`maxValue ${maxValue > 0 ? '' : ' _err'}`}>{numberWithCommas(availableStt)} STT ~ {numberWithCommas(availableUsdt.toFixed(2))} USDT</span> : ''}
                    </div>
                </div>
                <div className={"swap_block-to"}>
                    <div className={"to_number stt-input"} style={{textAlign: "center"}}>{targetValue}</div>
                    <div onClick={props.withoutWallet ? () => setWwModal(true) : undefined}>
                        <Select defaultValue={to_options[sourceToken][0]}
                                options={to_options[sourceToken]}
                                components={{
                                    Option: IconOption,
                                    SingleValue: ValueOption,
                                    IndicatorsContainer: CustomIndicator
                                }}
                                className="react-select-container donation-select"
                                classNamePrefix="react-select"
                                onChange={changeTargetToken}
                                value={to_options[sourceToken][to_list[sourceToken].indexOf(targetToken)]}
                                isSearchable={false}
                                isDisabled={props.withoutWallet}
                                // onClick={props.withoutWallet ? () => setWwModal(true) : undefined}
                        />
                    </div>
                    </div>
                    {targetToken !== 'eth'
                        ? <div className={"swap_block-action"}>
                            <div className={"info-block"}>
                                {!props.withoutWallet
                                    ? <React.Fragment>
                                        <span>Swap rate</span>
                                        {targetToken === 'usdt'
                                            ?
                                            <span>1 STT = {rate ? (parseFloat(rate.toString().replace(',', '.')) * (1 - (fee / 100))).toFixed(6) : 0.000} USDT</span>
                                            : <React.Fragment>
                                                {targetToken === 'stt'
                                                    ? <span>1 STT = {correctRate.toFixed(8)} USDT</span>
                                                    : <span>1 STT = {extraBalances[targetToken].rate ? extraBalances[targetToken].rate : 0.000} {extraBalances[targetToken].name}</span>
                                                }
                                            </React.Fragment>
                                        }
                                        <span className={"green-span"}>Update 12:15 GMT</span>
                                    </React.Fragment>
                                    : ''
                                }
                            </div>
                            <div className={"swap__confirm"}>
                                {maxValue > 0
                                    ? <div className={"swap__confirm-button"} onClick={handleSwap}>
                                        <i className="fa-solid fa-shuffle"></i>
                                    </div>
                                    : <div className={"swap__confirm-button _disabled"}
                                           onClick={props.withoutWallet ? () => setWwModal(true) : undefined}>
                                        <i className="fa-solid fa-shuffle"></i>
                                    </div>
                                }
                            </div>
                        </div>
                        : <div className={"swap_block-action"}>
                            <div className={"info-block"}>
                                <span>To swap</span>
                                <span>USDT to ETH go to</span>
                                <a className={"green-span"}
                                   href={"https://www.sushi.com/swap?chainId=42161&token0=0xFd086bC7CD5C481DCC9C85ebE478A1C0b69FCbb9&token1=NATIVE"}
                                   target={"_blank"} rel={"noreferrer"}>Sushi.com</a>
                            </div>
                            <div className={"swap__confirm"}>
                                <a className={"swap__confirm-button _link"}
                                   href={"https://www.sushi.com/swap?chainId=42161&token0=0xFd086bC7CD5C481DCC9C85ebE478A1C0b69FCbb9&token1=NATIVE"}
                                   target={"_blank"} rel={"noreferrer"}>
                                    go
                                </a>
                            </div>
                        </div>
                    }

                    <div className={"preloader"} style={preloader}>
                        <Grid
                            height="80"
                            width="80"
                            color="#ffffff"
                            ariaLabel="grid-loading"
                            radius="12.5"
                            wrapperStyle={{}}
                            wrapperClass=""
                            visible={true}
                        />
                    </div>
                </div>

                <Toast onClose={() => window.location.reload()} show={toastCompleteShow} onClick={() => setToastCompleteShow(false)} autohide delay={5000} className={"complete-toast"}>
                <Toast.Body>
                    <i className="fa-solid fa-circle-check" style={{fontSize: '6rem', margin: 20, color: '#96fac5'}}></i>
                    <p style={{fontWeight: 600}}>SUCCESS</p>
                    <p className={"complete-toast-text"}>Please, refresh the page</p>
                </Toast.Body>
            </Toast>
            <Toast onClose={() => setToastErrorShow(false)} show={toastErrorShow} onClick={() => setToastErrorShow(false)} autohide delay={5000}  className={"complete-toast"}>
                <Toast.Body>
                    <i className="fa-solid fa-circle-xmark" style={{fontSize: '6rem', margin: 20, color: '#ff968f'}}></i>
                    <p className={"toast-err"} style={{fontWeight: 600, color: '#dc3545'}}>ERROR</p>
                    <p className={"complete-toast-text"}>{toastText}</p>
                </Toast.Body>
            </Toast>
            <Modal size="sm" show={showWwModal} onHide={() => setWwModal(false)} aria-labelledby="info-mod-title" className={"pre-form-modal telegram-modal"} centered >
                <Modal.Body>
                    <div className={"stt_modal_header"}>


                    </div>
                    <div className={"help-wrapper telegram-wrapper"} style={{paddingLeft: 0, paddingRight: 0}}>
                        <img src={"/img/lock.png"} alt={''} style={{width: 80, marginTop: 70}} />
                        <p style={{fontSize: '1.1rem', marginBottom: 20, marginTop: 30, color: '#888888'}}>Authorization</p>
                        <p style={{fontSize: '.8rem', marginTop: 0, fontWeight: 400, marginBottom: 25}}>To use full functionality, login to the<br/>website with the browser of your crypto<br/>wallet</p>
                        <a href={"/login.pdf"} target={"_blank"} rel={"noreferrer"} style={{fontSize: '.8rem', marginTop: 0, fontWeight: 500, display: "block", marginBottom: 45, color: '#47c999'}}>Instructions</a>

                        <Button className="modal-button"  onClick={() => setWwModal(false)}>Ok</Button>
                    </div>
                </Modal.Body>
            </Modal>
        </React.Fragment>
    )
}

export default Swap;